    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        // TODO add your handling code here:
        this.setLocationRelativeTo(null);
        cbo_com.removeAllItems();
        cbo_nsinh.removeAllItems(); // xóa item cũ
        for (int i = 1990; i <= 2015; i++) {
            String t = i + "";
            // cách 2 String t1=String.valueOf(i);
            cbo_nsinh.addItem(t);
        }
        rdo_ruou.setSelected(true);
        rdo_ruou.isSelected();
        cbo_com.addItem("CƠM RANG");
        cbo_com.addItem("CƠM NẾP");
        cbo_com.addItem("CƠM XÔI");
        cbo_com.addItem("CƠM LAM");

    }//GEN-LAST:event_formWindowOpened

