    public void formTable() {
        lap4class u1 = new lap4class("THINH", "2003", "NUOC NGOT", "COM RANG");
        lap4class u2 = new lap4class("TU", "2006", "NUOC NGOT", "COM LAM");
        lap4class u3 = new lap4class("HUY", "1999", "BIA", "XÔI");
        list.add(u1);
        list.add(u2);
        list.add(u3);
        fillTable();
    }
    ArrayList<lap4class> list = new ArrayList<>();

    void fillTable() {
        DefaultTableModel mol = (DefaultTableModel) tbl_table.getModel();

        for (lap4class x : list) {
            Object[] data = new Object[]{
                x.getHoTen(), x.getNamSinh(), x.getDoUong(), x.getCom()};
            mol.addRow(data);
        }
    }

    lap4class readForm() {
        String ten, namsinh, douong, com;
        ten = txt_name.getText();
        namsinh = cbo_nsinh.getToolTipText();
        com = cbo_com.getToolTipText();

        if (rdo_ruou.isSelected()) {
            douong = "RUOu";
        } else if (rdo_bia.isSelected()) {
            douong = "BIA";
        } else {
            douong = "NUOC NGOT";
        }
        return new lap4class(ten, namsinh, douong, com);
    }

    void writeForm(lap4class u) {
        txt_name.setText(u.getHoTen());
        cbo_nsinh.setToolTipText(u.getNamSinh());
        cbo_com.setToolTipText(u.getCom());
        if (u.getDoUong().equalsIgnoreCase("BIA")) {
            rdo_bia.setSelected(true);
        } else if (u.getDoUong().equalsIgnoreCase("RUOC")) {
            rdo_ruou.setSelected(true);
        } else {
            rdo_ngot.setSelected(true);
        }
    }
    
    
    void addUser() {
        list.add(readForm());
    }
    int getRow() {
        return tbl_table.getSelectedRow();
    }
    void removeUser() {
        int index = this.getRow();
        list.remove(index);
    }

    void clearForm() {
        txt_name.setText("");
        cbo_nsinh.setToolTipText("");
        rdo_bia.setText("");
        rdo_ruou.setText("");
        rdo_ngot.setText("");

        cbo_com.setToolTipText("");

    }
    void updateUser(){
    int index = this.getRow();
    lap4class u=this.readForm();
    list.set(index, u);
    }
